import { useEffect, useState } from "react";
import { listarUsuarios, criarUsuario, excluirUsuario } from "../services/usuariosApi";
import { Button, Input, Select } from "../components/ui";

export default function Usuarios() {
  const [usuarios, setUsuarios] = useState([]);
  const [carregando, setCarregando] = useState(false);
  const [erro, setErro] = useState(null);
  const [sucesso, setSucesso] = useState(null);
  const [form, setForm] = useState({
    nome: "",
    email: "",
    senha: "",
    tipo: "comum",
    admin: false,
    ativo: true,
  });

  async function carregarUsuarios() {
    setCarregando(true);
    setErro(null);
    try {
      const data = await listarUsuarios();
      setUsuarios(data);
    } catch (error) {
      setErro(error.message || "Erro ao carregar usuários");
    } finally {
      setCarregando(false);
    }
  }

  useEffect(() => {
    carregarUsuarios();
  }, []);

  function handleChange(e) {
    const { name, value, type, checked } = e.target;
    setForm((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  }

  async function handleSubmit(e) {
    e.preventDefault();

    if (!form.nome || !form.email || !form.senha) {
      setErro("Nome, email e senha são obrigatórios");
      return;
    }

    setErro(null);
    setSucesso(null);
    try {
      await criarUsuario(form);
      setForm({
        nome: "",
        email: "",
        senha: "",
        tipo: "comum",
        admin: false,
        ativo: true,
      });
      setSucesso("Usuário criado com sucesso");
      carregarUsuarios();
    } catch (error) {
      setErro(error.message || "Erro ao criar usuário");
    }
  }

  async function handleExcluir(id) {
    if (!window.confirm("Deseja excluir este usuário?")) return;
    setErro(null);
    setSucesso(null);
    try {
      await excluirUsuario(id);
      setSucesso("Usuário excluído com sucesso");
      carregarUsuarios();
    } catch (error) {
      setErro(error.message || "Erro ao excluir usuário");
    }
  }

  return (
    <div>
      <div className="page-header">
        <h2>Usuários</h2>
        <p className="page-subtitle">Cadastro e controle de acesso</p>
      </div>

      <div className="chamados-layout">
        <form onSubmit={handleSubmit} className="form-card">
          {erro && <div className="alert alert-error">{erro}</div>}
          {sucesso && <div className="alert alert-success">{sucesso}</div>}
          <Input
            label="Nome"
            name="nome"
            placeholder="Nome completo"
            value={form.nome}
            onChange={handleChange}
            required
          />

          <Input
            label="Email"
            type="email"
            name="email"
            placeholder="email@exemplo.com"
            value={form.email}
            onChange={handleChange}
            required
          />

          <Input
            label="Senha"
            type="password"
            name="senha"
            placeholder="Senha inicial"
            value={form.senha}
            onChange={handleChange}
            required
          />

          <Select label="Tipo" name="tipo" value={form.tipo} onChange={handleChange}>
            <option value="comum">Comum</option>
            <option value="ti">TI</option>
          </Select>

          <label className="checkbox-field">
            <input
              type="checkbox"
              name="admin"
              checked={form.admin}
              onChange={handleChange}
            />
            Administrador
          </label>

          <label className="checkbox-field">
            <input
              type="checkbox"
              name="ativo"
              checked={form.ativo}
              onChange={handleChange}
            />
            Ativo
          </label>

          <div className="form-actions">
            <Button type="submit" variant="primary">
              Criar usuário
            </Button>
          </div>
        </form>

        <div className="table-card">
          <div className="table-header">
            <strong>Usuários cadastrados</strong>
          </div>
          <table className="chamados-table">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Email</th>
                <th>Tipo</th>
                <th>Admin</th>
                <th>Ativo</th>
                <th className="acoes-col">Ações</th>
              </tr>
            </thead>
            <tbody>
              {carregando && (
                <tr>
                  <td colSpan="6">Carregando...</td>
                </tr>
              )}
              {!carregando && usuarios.length === 0 && (
                <tr>
                  <td colSpan="6">Nenhum usuário encontrado.</td>
                </tr>
              )}
              {usuarios.map((u) => (
                <tr key={u.id}>
                  <td data-label="Nome">{u.nome}</td>
                  <td data-label="Email">{u.email}</td>
                  <td data-label="Tipo">{u.tipo}</td>
                  <td data-label="Admin">{u.admin ? "Sim" : "Não"}</td>
                  <td data-label="Ativo">{u.ativo ? "Sim" : "Não"}</td>
                  <td data-label="Ações" className="cell-actions">
                    <Button
                      type="button"
                      variant="danger"
                      size="sm"
                      onClick={() => handleExcluir(u.id)}
                    >
                      Excluir
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
