import { useEffect, useRef, useState } from "react";
import { useChamados } from "../contextos/chamadosContext";

/* ============================
   DEMANDAS PADRONIZADAS
============================ */
const DEMANDAS_PADRAO = [
  "SUPORTE - Manuten√ß√£o de Hardware",
  "SUPORTE - Instala√ß√£o de Perif√©ricos",
  "SUPORTE - Lentid√£o e Performance",
  "SUPORTE - Formata√ß√£o e Reinstala√ß√£o",
  "SUPORTE - Reset de Senha",
  "SUPORTE - Gest√£o de Usu√°rios (Onboarding)",
  "SUPORTE - Desativa√ß√£o de Contas (Offboarding)",
  "SUPORTE - VPN e Acesso Remoto",
  "SUPORTE - Varredura de Seguran√ßa",
  "SUPORTE - Instala√ß√£o de Software",
  "SUPORTE - Erro de Aplica√ß√£o",
  "SUPORTE - Configura√ß√£o de E-mail",
  "SUPORTE - Backup e Restaura√ß√£o",
  "SUPORTE - Conectividade de Rede",
  "SUPORTE - Telefonia e VoIP",
  "SUPORTE - Libera√ß√£o de Firewall",
  "SUPORTE - Infraestrutura de Servidor",
];

/* Enter funciona como TAB */
function handleEnterAsTab(e) {
  if (e.key !== "Enter") return;

  const form = e.target.form;
  if (!form) return;

  const elements = Array.from(form.elements).filter(
    (el) =>
      el.tagName !== "BUTTON" &&
      !el.disabled &&
      el.type !== "hidden"
  );

  const index = elements.indexOf(e.target);

  if (index > -1 && index < elements.length - 1) {
    e.preventDefault();
    elements[index + 1].focus();
  }
}

export default function Chamados() {
  const { chamados, criarChamado, carregando } = useChamados();

  const [lista, setLista] = useState([]);
  const [editandoId, setEditandoId] = useState(null);

  const [form, setForm] = useState({
    responsavel: "",
    data: "",
    demanda: "",
    status: "Aberto",
    obs: "",
  });

  const responsavelRef = useRef(null);

  /* Sincroniza lista local com contexto */
  useEffect(() => {
    setLista(chamados);
  }, [chamados]);

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function editarChamado(chamado) {
    setForm({
      responsavel: chamado.responsavel || "",
      data: chamado.data || "",
      demanda: chamado.demanda || "",
      status: chamado.status || "Aberto",
      obs: chamado.obs || "",
    });
    setEditandoId(chamado.id);
    responsavelRef.current?.focus();
  }

  function excluirChamado(id) {
    if (!window.confirm("Deseja realmente excluir este chamado?")) return;

    const novaLista = lista.filter((c) => c.id !== id);
    setLista(novaLista);
    localStorage.setItem(
      "@chamados-ti:chamados",
      JSON.stringify(novaLista)
    );
  }

  async function handleSubmit(e) {
    e.preventDefault();

    if (editandoId) {
      const atualizada = lista.map((c) =>
        c.id === editandoId ? { ...c, ...form } : c
      );
      setLista(atualizada);
      localStorage.setItem(
        "@chamados-ti:chamados",
        JSON.stringify(atualizada)
      );
      setEditandoId(null);
    } else {
      await criarChamado(form);
    }

    setForm({
      responsavel: "",
      data: "",
      demanda: "",
      status: "Aberto",
      obs: "",
    });

    responsavelRef.current?.focus();
  }

  function formatarDataBR(data) {
    if (!data) return "";
    const [ano, mes, dia] = data.split("-");
    return `${dia}/${mes}/${ano}`;
  }

  function badgeClass(status) {
    return `badge ${(status || "Aberto")
      .toLowerCase()
      .replace(/\s+/g, "-")}`;
  }

  function estaAtrasado(chamado) {
    if (!chamado.data) return false;
    if (chamado.status === "Conclu√≠do") return false;
    const hoje = new Date().toISOString().split("T")[0];
    return chamado.data < hoje;
  }

  if (carregando) {
    return (
      <div className="container">
        <p>Carregando chamados...</p>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="page-header">
        <h2>Chamados</h2>
        <p>Registro e acompanhamento de chamados</p>
      </div>

      <form onSubmit={handleSubmit} className="form-bar">
        <div>
          <label>Respons√°vel</label>
          <input
            ref={responsavelRef}
            name="responsavel"
            value={form.responsavel}
            onChange={handleChange}
            onKeyDown={handleEnterAsTab}
          />
        </div>

        <div>
          <label>Data</label>
          <input
            type="date"
            name="data"
            value={form.data}
            onChange={handleChange}
            onKeyDown={handleEnterAsTab}
          />
        </div>

        <div>
          <label>Demanda</label>
          <select
            name="demanda"
            value={form.demanda}
            onChange={handleChange}
            onKeyDown={handleEnterAsTab}
          >
            <option value="">Selecione</option>
            {DEMANDAS_PADRAO.map((d, i) => (
              <option key={i} value={d}>
                {d}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Status</label>
          <select
            name="status"
            value={form.status}
            onChange={handleChange}
            onKeyDown={handleEnterAsTab}
          >
            <option>Aberto</option>
            <option>Em Atendimento</option>
            <option>Conclu√≠do</option>
          </select>
        </div>

        <div>
          <label>Obs</label>
          <input
            name="obs"
            value={form.obs}
            onChange={handleChange}
            onKeyDown={handleEnterAsTab}
          />
        </div>

        <button type="submit" className="btn btn-primary">
          {editandoId ? "Atualizar" : "Salvar"}
        </button>
      </form>

      <h3>Chamados Registrados</h3>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Respons√°vel</th>
            <th>Status</th>
            <th>Data</th>
            <th>Demanda</th>
            <th>Obs</th>
            <th>A√ß√µes</th>
          </tr>
        </thead>
        <tbody>
          {lista.map((c) => {
            const atrasado = estaAtrasado(c);
            return (
              <tr
                key={c.id}
                className={atrasado ? "linha-atrasada" : ""}
              >
                <td>{c.id}</td>
                <td>{c.responsavel}</td>
                <td>
                  <span className={badgeClass(c.status)}>
                    {c.status}
                  </span>
                  {atrasado && (
                    <span className="badge atrasado">
                      Atrasado
                    </span>
                  )}
                </td>
                <td>{formatarDataBR(c.data)}</td>
                <td>{c.demanda}</td>
                <td className="obs">{c.obs}</td>
                <td>
                  <div className="actions">
                    <button onClick={() => editarChamado(c)}>
                      ‚úèÔ∏è
                    </button>
                    <button onClick={() => excluirChamado(c.id)}>
                      üóëÔ∏è
                    </button>
                  </div>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}
