const STORAGE_KEY = "@chamados-ti:chamados";

export const STATUS_CHAMADO = {
  ABERTO: "ABERTO",
  EM_ATENDIMENTO: "EM_ATENDIMENTO",
  AGUARDANDO_CLIENTE: "AGUARDANDO_CLIENTE",
  AGUARDANDO_TERCEIRO: "AGUARDANDO_TERCEIRO",
  RESOLVIDO: "RESOLVIDO",
  FECHADO: "FECHADO",
};

export const PRIORIDADE = {
  BAIXA: "BAIXA",
  MEDIA: "MEDIA",
  ALTA: "ALTA",
  CRITICA: "CRITICA",
};

/* PRIORIDADE AUTOMÁTICA */
function calcularPrioridade(categoria, impacto) {
  if (impacto === "ALTO") return PRIORIDADE.CRITICA;
  if (categoria === "INFRA") return PRIORIDADE.ALTA;
  return PRIORIDADE.MEDIA;
}

/* SLA BASE */
function calcularSLA(prioridade) {
  switch (prioridade) {
    case PRIORIDADE.CRITICA:
      return 4;
    case PRIORIDADE.ALTA:
      return 8;
    case PRIORIDADE.MEDIA:
      return 24;
    default:
      return 48;
  }
}

export async function listarChamados(empresaId) {
  const dados = localStorage.getItem(STORAGE_KEY);
  const chamados = dados ? JSON.parse(dados) : [];
  return chamados.filter(c => c.empresaId === empresaId);
}

export async function salvarChamado(empresaId, chamado) {
  const dados = localStorage.getItem(STORAGE_KEY);
  const chamados = dados ? JSON.parse(dados) : [];

  const prioridade = calcularPrioridade(
    chamado.categoria,
    chamado.impacto
  );

  const novoChamado = {
    ...chamado,
    id: Date.now(),
    empresaId,
    status: STATUS_CHAMADO.ABERTO,
    prioridade,
    slaHoras: calcularSLA(prioridade),
    criadoEm: new Date().toISOString(),
    fechadoEm: null,
    historico: [
      {
        data: new Date().toISOString(),
        acao: "Chamado criado",
      },
    ],
    comentarios: [],
  };

  chamados.push(novoChamado);
  localStorage.setItem(STORAGE_KEY, JSON.stringify(chamados));
  return novoChamado;
}

export async function atualizarChamadoStorage(empresaId, id, dadosAtualizados) {
  const dados = localStorage.getItem(STORAGE_KEY);
  const chamados = dados ? JSON.parse(dados) : [];

  const index = chamados.findIndex(
    c => c.id === id && c.empresaId === empresaId
  );

  const chamado = chamados[index];

  chamados[index] = {
    ...chamado,
    ...dadosAtualizados,
    historico: [
      ...chamado.historico,
      {
        data: new Date().toISOString(),
        acao: "Chamado atualizado",
      },
    ],
  };

  localStorage.setItem(STORAGE_KEY, JSON.stringify(chamados));
  return chamados[index];
}
