import { useState } from "react";
import { useChamados } from "../contextos/chamadosContext";
import { STATUS_CHAMADO } from "../services/chamadosService";

const STATUS_LABEL = {
  ABERTO: "Aberto",
  EM_ANDAMENTO: "Em andamento",
  AGUARDANDO_USUARIO: "Aguardando usuário",
  RESOLVIDO: "Resolvido",
};

export default function Chamados() {
  const {
    chamados,
    criarChamado,
    atualizarChamado,
    chamadoEmEdicao,
    setChamadoEmEdicao,
  } = useChamados();

  const [mostrarForm, setMostrarForm] = useState(false);
  const [form, setForm] = useState({
    responsavel: "",
    data: "",
    demanda: "",
    status: STATUS_CHAMADO.ABERTO,
    obs: "",
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function limparFormulario() {
    setForm({
      responsavel: "",
      data: "",
      demanda: "",
      status: STATUS_CHAMADO.ABERTO,
      obs: "",
    });
    setChamadoEmEdicao(null);
    setMostrarForm(false);
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.responsavel || !form.data || !form.demanda) return;

    chamadoEmEdicao
      ? atualizarChamado(chamadoEmEdicao.id, form)
      : criarChamado(form);

    limparFormulario();
  }

  function editarChamado(chamado) {
    setChamadoEmEdicao(chamado);
    setForm(chamado);
    setMostrarForm(true);
  }

  return (
    <>
      <div className="page-header">
        <h2>Chamados</h2>
        <p className="page-subtitle">
          Registro e acompanhamento de chamados
        </p>
      </div>

      {/* AÇÃO PRINCIPAL */}
      <div className="page-actions">
        <button
          className="btn-primary"
          onClick={() => setMostrarForm(!mostrarForm)}
        >
          {mostrarForm ? "Cancelar" : "Novo chamado"}
        </button>
      </div>

      {/* FORMULÁRIO */}
      {mostrarForm && (
        <div className="form-card">
          <h3>{chamadoEmEdicao ? "Editar chamado" : "Novo chamado"}</h3>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Responsável</label>
              <input
                name="responsavel"
                value={form.responsavel}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Data</label>
              <input
                type="date"
                name="data"
                value={form.data}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Demanda</label>
              <input
                name="demanda"
                value={form.demanda}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Status</label>
              <select
                name="status"
                value={form.status}
                onChange={handleChange}
              >
                {Object.values(STATUS_CHAMADO).map((s) => (
                  <option key={s} value={s}>
                    {STATUS_LABEL[s]}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Observações</label>
              <textarea
                name="obs"
                value={form.obs}
                onChange={handleChange}
              />
            </div>

            <div className="form-actions">
              <button className="btn-primary" type="submit">
                Salvar
              </button>
            </div>
          </form>
        </div>
      )}

      {/* LISTA EM CARDS */}
      <div className="chamados-cards">
        {chamados.map((c) => (
          <div key={c.id} className="chamado-card">
            <div className="chamado-topo">
              <span className={`status status-${c.status.toLowerCase()}`}>
                {STATUS_LABEL[c.status]}
              </span>
              <span className="data">{c.data}</span>
            </div>

            <h4 className="demanda">{c.demanda}</h4>

            <p className="responsavel">
              Responsável: <strong>{c.responsavel}</strong>
            </p>

            <div className="chamado-acoes">
              <button onClick={() => editarChamado(c)}>Editar</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
