import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../contextos/authContext";

export default function Login() {
  const { login, estaAutenticado, carregando } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");

  // rota de destino após login (vinda da RotaPrivada)
  const from = location.state?.from?.pathname || "/";

  function handleSubmit(e) {
    e.preventDefault();

    if (!email || !senha) {
      alert("Informe email e senha");
      return;
    }

    login(email, senha);
  }

  useEffect(() => {
    if (!carregando && estaAutenticado) {
      navigate(from, { replace: true });
    }
  }, [estaAutenticado, carregando, navigate, from]);

  // evita piscar / redirecionar antes do contexto carregar
  if (carregando) {
    return null;
  }

  return (
    <div className="center-page">
      <div className="open-ticket">
        <h2>Login</h2>
        <p>Informe suas credenciais</p>

        <form onSubmit={handleSubmit} className="open-form">
          <div>
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="email@exemplo.com"
            />
          </div>

          <div>
            <label>Senha</label>
            <input
              type="password"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              placeholder="********"
            />
          </div>

          <div className="form-actions center">
            <button className="btn-primary">Entrar</button>
          </div>
        </form>
      </div>
    </div>
  );
}
