import { useContext } from "react";
import { ChamadosContext } from "../contextos/chamadosContext";

export default function Inicio() {
  const { chamados } = useContext(ChamadosContext);

  const total = chamados.length;

  const abertos = chamados.filter(
    c => (c.status || "Aberto") === "Aberto"
  ).length;

  const atendimento = chamados.filter(
    c => (c.status || "Aberto") === "Em Atendimento"
  ).length;

  const concluidos = chamados.filter(
    c => (c.status || "Aberto") === "Concluído"
  ).length;

  return (
    <div className="container">
      {/* HEADER DA PÁGINA */}
      <div className="page-header">
        <h2>Dashboard</h2>
        <span className="page-subtitle">
          Visão geral dos chamados de TI
        </span>
      </div>

      {/* CARDS DE STATUS */}
      <div className="dashboard-grid">
        <div className="dashboard-card highlight">
          <span className="card-title">Total de Chamados</span>
          <span className="card-value">{total}</span>
        </div>

        <div className="dashboard-card status aberto">
          <span className="card-title">Abertos</span>
          <span className="card-value">{abertos}</span>
        </div>

        <div className="dashboard-card status atendimento">
          <span className="card-title">Em Atendimento</span>
          <span className="card-value">{atendimento}</span>
        </div>

        <div className="dashboard-card status concluido">
          <span className="card-title">Concluídos</span>
          <span className="card-value">{concluidos}</span>
        </div>
      </div>
    </div>
  );
}
