import { useState } from "react";
import { useChamados } from "../contextos/chamadosContext";
import { STATUS_CHAMADO } from "../services/chamadosService";

const STATUS_LABEL = {
  ABERTO: "Aberto",
  EM_ATENDIMENTO: "Em atendimento",
  AGUARDANDO_CLIENTE: "Aguardando cliente",
  AGUARDANDO_TERCEIRO: "Aguardando terceiro",
  RESOLVIDO: "Resolvido",
  FECHADO: "Fechado",
};

export default function Chamados() {
  const {
    chamados,
    criarChamado,
    atualizarChamado,
    chamadoEmEdicao,
    setChamadoEmEdicao,
  } = useChamados();

  const [form, setForm] = useState({
    responsavel: "",
    data: "",
    demanda: "",
    status: STATUS_CHAMADO.ABERTO,
    obs: "",
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function limpar() {
    setChamadoEmEdicao(null);
    setForm({
      responsavel: "",
      data: "",
      demanda: "",
      status: STATUS_CHAMADO.ABERTO,
      obs: "",
    });
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.responsavel || !form.demanda) return;

    chamadoEmEdicao
      ? atualizarChamado(chamadoEmEdicao.id, form)
      : criarChamado(form);

    limpar();
  }

  function editarChamado(c) {
    if (c.status === STATUS_CHAMADO.FECHADO) return;
    setChamadoEmEdicao(c);
    setForm(c);
  }

  function reabrirChamado(c) {
    atualizarChamado(c.id, {
      status: STATUS_CHAMADO.EM_ATENDIMENTO,
    });
  }

  return (
    <>
      <div className="page-header">
        <h2>Chamados</h2>
        <p className="page-subtitle">Gestão completa do fluxo de atendimento</p>
      </div>

      <div className="chamados-layout">
        <div className="form-card">
          <h3>{chamadoEmEdicao ? "Editar chamado" : "Novo chamado"}</h3>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Responsável</label>
              <input
                name="responsavel"
                value={form.responsavel}
                onChange={handleChange}
                disabled={chamadoEmEdicao?.status === STATUS_CHAMADO.FECHADO}
              />
            </div>

            <div className="form-group">
              <label>Demanda</label>
              <input
                name="demanda"
                value={form.demanda}
                onChange={handleChange}
                disabled={chamadoEmEdicao?.status === STATUS_CHAMADO.FECHADO}
              />
            </div>

            <div className="form-group">
              <label>Status</label>
              <select
                name="status"
                value={form.status}
                onChange={handleChange}
                disabled={chamadoEmEdicao?.status === STATUS_CHAMADO.FECHADO}
              >
                {Object.values(STATUS_CHAMADO).map(s => (
                  <option key={s} value={s}>
                    {STATUS_LABEL[s]}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Observações</label>
              <textarea
                name="obs"
                value={form.obs}
                onChange={handleChange}
              />
            </div>

            <div className="form-actions">
              <button className="btn-primary" type="submit">
                Salvar
              </button>
            </div>
          </form>
        </div>

        <div className="table-card">
          <div className="table-header">
            <h3>Chamados</h3>
          </div>

          <table className="chamados-table">
            <thead>
              <tr>
                <th>Status</th>
                <th>Demanda</th>
                <th>Responsável</th>
                <th>Ações</th>
              </tr>
            </thead>

            <tbody>
              {chamados.map(c => (
                <tr key={c.id}>
                  <td>
                    <span className={`status status-${c.status.toLowerCase()}`}>
                      {STATUS_LABEL[c.status]}
                    </span>
                  </td>
                  <td>{c.demanda}</td>
                  <td>{c.responsavel}</td>
                  <td>
                    {c.status === STATUS_CHAMADO.FECHADO ? (
                      <button onClick={() => reabrirChamado(c)}>
                        Reabrir
                      </button>
                    ) : (
                      <button onClick={() => editarChamado(c)}>
                        Editar
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}
