import { useState } from "react";
import { useChamados } from "../contextos/chamadosContext";

export default function Chamados() {
  const {
    chamados,
    criarChamado,
    atualizarChamado,
    chamadoEmEdicao,
    setChamadoEmEdicao,
  } = useChamados();

  const [form, setForm] = useState({
    responsavel: "",
    data: "",
    demanda: "",
    status: "Aberto",
    obs: "",
  });

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value });
  }

  function limparFormulario() {
    setForm({
      responsavel: "",
      data: "",
      demanda: "",
      status: "Aberto",
      obs: "",
    });
    setChamadoEmEdicao(null);
  }

  function handleSubmit(e) {
    e.preventDefault();

    if (!form.responsavel || !form.data || !form.demanda) {
      alert("Preencha os campos obrigat√≥rios");
      return;
    }

    if (chamadoEmEdicao) {
      atualizarChamado(chamadoEmEdicao.id, form);
    } else {
      criarChamado(form);
    }

    limparFormulario();
  }

  function editarChamado(chamado) {
    setChamadoEmEdicao(chamado);
    setForm({
      responsavel: chamado.responsavel,
      data: chamado.data,
      demanda: chamado.demanda,
      status: chamado.status,
      obs: chamado.obs,
    });
  }

  return (
    <div>
      {/* CABE√áALHO */}
      <div className="page-header">
        <h2>Chamados</h2>
        <p className="page-subtitle">
          Registro e acompanhamento de chamados
        </p>
      </div>

      <div className="chamados-layout">
        {/* FORMUL√ÅRIO */}
        <div className="form-card">
          <h3>{chamadoEmEdicao ? "Editar Chamado" : "Novo Chamado"}</h3>

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Respons√°vel</label>
              <input
                name="responsavel"
                value={form.responsavel}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Data</label>
              <input
                type="date"
                name="data"
                value={form.data}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Demanda</label>
              <input
                name="demanda"
                value={form.demanda}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label>Status</label>
              <select
                name="status"
                value={form.status}
                onChange={handleChange}
              >
                <option>Aberto</option>
                <option>Em Atendimento</option>
                <option>Conclu√≠do</option>
              </select>
            </div>

            <div className="form-group">
              <label>Observa√ß√µes</label>
              <textarea
                name="obs"
                value={form.obs}
                onChange={handleChange}
              />
            </div>

            <div className="form-actions">
              <button className="btn-primary" type="submit">
                {chamadoEmEdicao ? "Atualizar" : "Salvar"}
              </button>
            </div>
          </form>
        </div>

        {/* TABELA */}
        <div className="table-card">
          <div className="table-header">
            <h3>Chamados Registrados</h3>
          </div>

          <div className="table-wrapper">
            <table>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Respons√°vel</th>
                  <th>Status</th>
                  <th>Data</th>
                  <th>Demanda</th>
                  <th>Obs</th>
                  <th>A√ß√µes</th>
                </tr>
              </thead>

              <tbody>
                {chamados.map((c) => (
                  <tr key={c.id}>
                    <td>{c.id}</td>
                    <td>{c.responsavel}</td>
                    <td>
                      <span
                        className={`badge ${
                          c.status === "Aberto"
                            ? "aberto"
                            : c.status === "Em Atendimento"
                            ? "atendimento"
                            : "concluido"
                        }`}
                      >
                        {c.status}
                      </span>
                    </td>
                    <td>{c.data}</td>
                    <td>{c.demanda}</td>
                    <td>{c.obs}</td>
                    <td>
                      <div className="actions">
                        <button onClick={() => editarChamado(c)}>
                          ‚úèÔ∏è
                        </button>
                        <button type="button">
                          üóëÔ∏è
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}
